  createTag: function(tag) {
    return function(dispatch) {
      return TagApi
        .createTag(tag)
        .then(tag => {
          dispatch(actionsFunction.createTagSuccess(tag));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createTagSuccess: function(tag) {
    return { type: types.CREATE_TAG_SUCCESS, payload: tag };
  },


