    //CRUD METHODS
    
    //CRUD - CREATE
    	
	public Item insert(Item obj) {
		Long id = jdbcTemplate.queryForObject("select max(_id) from `item`", new MapSqlParameterSource(), Long.class);
		obj.set_id(id == null ? 1 : id + 1);
		String sql = "INSERT INTO `item` (`_id`, `createdAt`,`desc`,`isPublic`,`name`,`category`,`createdBy`) VALUES (:id,:createdAt,:desc,:isPublic,:name, :category , :createdBy )";
			SqlParameterSource parameters = new MapSqlParameterSource()
		    .addValue("id", obj.get_id())
			.addValue("createdAt", obj.getCreatedAt())
			.addValue("desc", obj.getDesc())
			.addValue("isPublic", obj.getIsPublic())
			.addValue("name", obj.getName())
			.addValue("category", obj.getCategory())
			.addValue("createdBy", obj.getCreatedBy());
		
		jdbcTemplate.update(sql, parameters);
		return obj;
	}
	
    	
    //CRUD - REMOVE
    
	public void delete(Long id) {
		String sql = "DELETE FROM `Item` WHERE `_id`=:id";
		SqlParameterSource parameters = new MapSqlParameterSource()
			.addValue("id", id);
		
		String sql_tags = "DELETE FROM `item_tags` WHERE `id_Item`= :id";
		jdbcTemplate.update(sql_tags, parameters);
		jdbcTemplate.update(sql, parameters);
	}

    	
