    // External relation m:m tags
    public static class Item_tagsService {

    	public static ArrayList<Long> findBy_Item(Long id_Item) {
    		String sql = "select `id_Tag` from Item_tags WHERE `id_Item`=:id_Item";
    		SqlParameterSource parameters = new MapSqlParameterSource()
    			.addValue("id_Item", id_Item);
    	    
    	    List<Long> listId = jdbcTemplate.queryForList(sql, parameters, Long.class);
    		return (ArrayList<Long>) listId;
    	}
    	

    	public static void updateRelation(Long id_Item, ArrayList<Long> tags) {

    		// Delete not in array
    		String in = " and `id_Tag` NOT IN (:tags)";
    		String sql = "DELETE FROM Item_tags WHERE `id_Item`=:id_Item ";
    		
    		if (tags != null && tags.size() > 0)
    			sql = sql + in;
    			
    		SqlParameterSource parameters = new MapSqlParameterSource()
    			.addValue("id_Item", id_Item)
    			.addValue("tags", tags);
    		
    		jdbcTemplate.update(sql, parameters);
    		
    		// Get actual    		
    	    List<Long> actual = ItemService.Item_tagsService.findBy_Item(id_Item);
    	    
    		// Insert new
    		for (Long id_Tag : tags) {
    			if (actual.indexOf(id_Tag) == -1){
    				ItemService.Item_tagsService.insert(id_Item, id_Tag);
    			}
    		}
    		
    	}
    	

    	public static void insert(Long id_Item, Long id_Tag) {
    		Item.Item_tags obj = new Item.Item_tags();
			Long id = jdbcTemplate.queryForObject("select max(_id) FROM Item_tags", new MapSqlParameterSource(), Long.class);
			obj.set_id(id == null ? 1 : id + 1);
			
			String sql = "INSERT INTO Item_tags (`_id`, `id_Item`, `id_Tag` )	VALUES (:id, :id_Item, :id_Tag)";

			MapSqlParameterSource parameters = new MapSqlParameterSource()
				.addValue("id", obj.get_id())
				.addValue("id_Item", id_Item)
				.addValue("id_Tag", id_Tag);

			jdbcTemplate.update(sql, parameters);
    	}

    }
	

    
